import React, {useState} from 'react'

export default function TextArea(props) {

    const toUpperCase = ()=>{
        setText(text.toUpperCase())
    }
    const toLowerCase = ()=>{
        setText(text.toLowerCase())
    }
    const handleOnChange = (event)=>{
        setText(event.target.value)
    }
    const[text, setText] = useState("")
  return (
    <>
    <div>
        <h3>{props.heading}</h3>
        <div className="mb-3">
            <textarea className="form-control" value = {text} onChange={handleOnChange} id="box-el" rows="8"></textarea> 
        </div>
        <button className = "btn btn-outline-primary mx-1" onClick={toUpperCase}>To UpperCase</button>
        <button className = "btn btn-outline-primary mx-1" onClick={toLowerCase}>To LowerCase</button>
        <button className = "btn btn-outline-primary mx-1" onClick={toLowerCase}>To LowerCase</button>
    </div>
    <div className="container my-2">
        <h3>Your text summary</h3>
        <p>{text.split(" ").length} words and {text.length} characters</p>
        <p>You need {0.008 * text.split(" ").length} minutes to read the above entered text</p>
        <h3>Preview</h3>
        <p>{text}</p>

    </div>
    </>
  )
}
