import React, {useState} from 'react'

export default function TextArea(props) {

    const toUpperCase = ()=>{
        setText(text.toUpperCase())
    }
    const toLowerCase = ()=>{
        setText(text.toLowerCase())
    }
    const handleOnChange = (event)=>{
        setText(event.target.value)
    }
    const[text, setText] = useState('Enter text here')
  return (
    <>
    <div>
        <h1>{props.heading}</h1>
        <div className="mb-3">
            <textarea className="form-control" value = {text} onChange={handleOnChange} id="box-el" rows="8"></textarea> 
        </div>
        <button className = "btn btn-primary" onClick={toUpperCase}>To UpperCase</button>
        <button className = "btn btn-primary" onClick={toLowerCase}>To LowerCase</button>
    </div>
    <div className="container my-2">
        <h2>Your text summary</h2>
        <p>{text.split(" ").length} words and {text.length} characters</p>

    </div>
    </>
  )
}
